import React, { useState } from 'react';
import Selecto from "react-selecto";
import './ReservationPage.css';

const ReservationPage = () => {
    const slots = [];
    const days = ['MON', 'TUE', 'WED', 'THU', 'FRI', 'SAT', 'SUN'];
    const times = Array.from({ length: 24 }, (_, i) => `${i}:00-${i + 1}:00`);

    for (let i = 0; i < 24 * 7; ++i) {
        slots.push(i);
    }

    return (
        <div className="reservation-page">
            <div className="timeline">
                <Selecto
                    dragContainer={".slot"}
                    selectableTargets={[".selecto-area .slot"]}
                    hitRate={0}
                    selectByClick={true}
                    selectFromInside={true}
                    continueSelect={true}
                    ratio={0}
                    onSelect={e => {
                        e.added.forEach(el => {
                            el.classList.add("selected");
                        });
                        e.removed.forEach(el => {
                            el.classList.remove("selected");
                        });
                    }}
                />

                <div className="select-container">
                    <table>
                        <thead>
                            <tr>
                                <th>TIME</th>
                                {days.map(day => <th key={day}>{day}</th>)}
                            </tr>
                        </thead>
                        <tbody className="selecto-area">
                            {times.map((time, i) => (
                                <tr key={time}>
                                    <td>{time}</td>
                                    {days.map((day, j) => {
                                        const index = i * 7 + j;
                                        return <td className="slot" key={index}></td>;
                                    })}
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
            <div className="sidebar">
                <div className="participant-list">
                    <h3>참석자</h3>
                </div>
                <div>
                    <input type="text" placeholder="아이디를 입력해 주세요" />
                    <input type="text" placeholder="이메일 주소" />
                    <button>참석자 초대하기</button>
                </div>
                <div>
                    <input type="text" placeholder="회의 이름" />
                    <div>
                        <input type="text" placeholder="회의 시작 시간" />
                        <input type="text" placeholder="회의 끝 시간" />
                    </div>
                    <textarea placeholder="회의 내용을 입력하세요"></textarea>
                </div>
                <div>
                    <button>회의 예약하기</button>
                    <button onClick={() => window.location.href = '/MyConference'}>뒤로 돌아가기</button>
                </div>
            </div>
        </div>
    );
};

export default ReservationPage;
