import React, { useState } from 'react';
import './ReservationPage.css';

const ReservationPage = () => {
  const [selectedSlots, setSelectedSlots] = useState([]);
  
  const handleMouseDown = (timeSlot) => {
    if (selectedSlots.includes(timeSlot)) {
      setSelectedSlots(selectedSlots.filter(slot => slot !== timeSlot));
    } else {
      setSelectedSlots([...selectedSlots, timeSlot]);
    }
  };

  const handleMouseEnter = (timeSlot) => {
    if (!selectedSlots.includes(timeSlot)) {
      setSelectedSlots([...selectedSlots, timeSlot]);
    }
  };

  const handleMouseUp = () => {
    document.removeEventListener('mouseenter', handleMouseEnter);
  };

  const days = ['MON', 'TUE', 'WED', 'THU', 'FRI', 'SAT', 'SUN'];
  const times = Array.from({ length: 24 }, (_, i) => `${i}:00-${i + 1}:00`);

  return (
    <div className="reservation-page">
      <div className="timeline">
        <table>
          <thead>
            <tr>
              <th>TIME</th>
              {days.map(day => <th key={day}>{day}</th>)}
            </tr>
          </thead>
          <tbody>
            {times.map((time, i) => (
              <tr key={time}>
                <td>{time}</td>
                {days.map(day => {
                  const timeSlot = `${day}-${time}`;
                  return (
                    <td
                      key={timeSlot}
                      className={selectedSlots.includes(timeSlot) ? 'selected' : ''}
                      onMouseDown={() => handleMouseDown(timeSlot)}
                      onMouseEnter={() => handleMouseEnter(timeSlot)}
                      onMouseUp={handleMouseUp}
                    ></td>
                  );
                })}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <div className="sidebar">
        <div className="participant-list">
          <h3>참석자</h3>
        </div>
        <div>
          <input type="text" placeholder="아이디를 입력해 주세요" />
          <input type="text" placeholder="이메일 주소" />
          <button>참석자 초대하기</button>
        </div>
        <div>
          <input type="text" placeholder="회의 이름" />
          <div>
            <input type="text" placeholder="회의 시작 시간" />
            <input type="text" placeholder="회의 끝 시간" />
          </div>
          <textarea placeholder="회의 내용을 입력하세요"></textarea>
        </div>
        <div>
          <button>회의 예약하기</button>
          <button onClick={() => window.location.href = '/MyConference'}>뒤로 돌아가기</button>
        </div>
      </div>
    </div>
  );
};

export default ReservationPage;
