import React, { useState } from 'react';

function MeetingSummary() {
    const [meetingName, setMeetingName] = useState('');
    const [meetingDate, setMeetingDate] = useState('');
    const [summary, setSummary] = useState('');

    const handleSubmit = () => {
        console.log('Meeting Name:', meetingName);
        console.log('Meeting Date:', meetingDate);
        console.log('Summary:', summary);
        alert('저장되었습니다!');
    };

    return (
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'flex-start', width: '100%', margin: 'auto', paddingTop: '5vh' }}>
            <div style={{ width: '100%', maxWidth: '600px', border: '1px solid #ccc', padding: '20px' }}>
                <h1 style={{ textAlign: 'center' }}>AI 회의 요약</h1>
                <form onSubmit={(e) => {
                    e.preventDefault();
                    handleSubmit();
                }}>
                     <div>
                        <label style={{ flex: '1', fontWeight: 'bold', fontSize: '18px' }}>회의 이름</label>
                        <input
                            type="text"
                            value={meetingName}
                            onChange={(e) => setMeetingName(e.target.value)}
                            style={{ flex: '2', border: 'none', outline: 'none' }}
                        />
                    </div>
                    <div>
                        <label style={{ flex: '1', fontWeight: 'bold', fontSize: '18px' }}>회의 날짜</label>
                        <input
                            type="date"
                            value={meetingDate}
                            onChange={(e) => setMeetingDate(e.target.value)}
                            style={{ flex: '2', border: 'none', outline: 'none' }}
                        />
                    </div>
                    <div style={{ marginBottom: '20px', borderBottom: '1px solid #ccc' }}>
                        <label style={{ width: '30%', display: 'inline-block',fontWeight: 'bold', fontSize: '18px'  }}>회의 요약</label>
                        <textarea
                            value={summary}
                            onChange={(e) => setSummary(e.target.value)}
                            style={{ width: '100%', border: 'none', outline: 'none', resize: 'none', height: '400px' }}
                        />
                    </div>
                    <button type="submit" style={{ width: '100%', padding: '10px', backgroundColor: '#007BFF', color: 'white', border: 'none' }}>수정하기</button>
                </form>
            </div>
        </div>
    );
}

export default MeetingSummary;
