import React, { useState } from 'react';
import Selecto from "react-selecto";
import { useNavigate } from 'react-router-dom';

function ReservationPage() {
    const navigate = useNavigate();
    const [title, setTitle] = useState('');
    const [date, setDate] = useState('');
    const [timeSlots, setTimeSlots] = useState([]);

    const slots = [];
    for (let i = 0; i < 96; ++i) {
        slots.push(i);
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        // 예약 처리 로직 추가
        console.log(`예약 제목: ${title}, 날짜: ${date}, 시간 슬롯: ${timeSlots}`);
        navigate('/'); // 예약 후 메인 페이지로 이동
    };

    return (
        <div className="ReservationPage">
            <h1>회의 예약</h1>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="title">회의 제목:</label>
                    <input
                        type="text"
                        id="title"
                        value={title}
                        onChange={(e) => setTitle(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="date">날짜:</label>
                    <input
                        type="date"
                        id="date"
                        value={date}
                        onChange={(e) => setDate(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <label>시간 선택:</label>
                    <div className="select-container">
                        <Selecto
                            dragContainer={".slot"}
                            selectableTargets={[".selecto-area .slot"]}
                            hitRate={0}
                            selectByClick={true}
                            selectFromInside={true}
                            continueSelect={true}
                            ratio={0}
                            onSelect={e => {
                                const newSelected = [];
                                e.added.forEach(el => {
                                    el.classList.add("selected");
                                    newSelected.push(el.dataset.index);
                                });
                                e.removed.forEach(el => {
                                    el.classList.remove("selected");
                                    const index = newSelected.indexOf(el.dataset.index);
                                    if (index > -1) newSelected.splice(index, 1);
                                });
                                setTimeSlots(newSelected);
                            }}
                        />

                        <div className="elements selecto-area" id="selecto1">
                            {slots.map(i => {
                                let sep = "";
                                if (i % 4 === 3) {
                                    sep = " solid";
                                } else if (i % 4 === 1) {
                                    sep = " dotted";
                                }
                                return <div className={"slot" + sep} data-index={i} key={i}></div>
                            })}
                        </div>
                    </div>
                </div>
                <button type="submit" className="submit-button">예약하기</button>
            </form>
        </div>
    );
}

export default ReservationPage;
