import React, { useState } from 'react';
import { useParams } from 'react-router-dom';

function MeetingSummary() {
    const [meetingName, setMeetingName] = useState('');
    const [meetingDate, setMeetingDate] = useState('');
    const [summary, setSummary] = useState('');

    const handleSubmit = () => {
        console.log('Meeting Name:', meetingName);
        console.log('Meeting Date:', meetingDate);
        console.log('Summary:', summary);
        alert('저장되었습니다!');
    };

    return (
        <div style={{ width: '600px', margin: 'auto', border: '1px solid #ccc', padding: '20px' }}>
            <h1>AI 회의 요약</h1>
            <form onSubmit={(e) => {
                e.preventDefault();
                handleSubmit();
            }}>
                <div style={{ marginBottom: '20px', borderBottom: '1px solid #ccc' }}>
                    <label style={{ width: '30%', display: 'inline-block' }}>회의 이름:</label>
                    <input
                        type="text"
                        value={meetingName}
                        onChange={(e) => setMeetingName(e.target.value)}
                        style={{ width: '70%', border: 'none', outline: 'none' }}
                    />
                </div>
                <div style={{ marginBottom: '20px', borderBottom: '1px solid #ccc' }}>
                    <label style={{ width: '30%', display: 'inline-block' }}>회의 날짜:</label>
                    <input
                        type="date"
                        value={meetingDate}
                        onChange={(e) => setMeetingDate(e.target.value)}
                        style={{ width: '70%', border: 'none', outline: 'none' }}
                    />
                </div>
                <div style={{ marginBottom: '20px', borderBottom: '1px solid #ccc' }}>
                    <label style={{ width: '30%', display: 'inline-block' }}>회의 요약:</label>
                    <textarea
                        value={summary}
                        onChange={(e) => setSummary(e.target.value)}
                        style={{ width: '70%', border: 'none', outline: 'none', resize: 'none', height: '100px' }}
                    />
                </div>
                <button type="submit" style={{ width: '100%', padding: '10px', backgroundColor: '#007BFF', color: 'white', border: 'none' }}>저장하기</button>
            </form>
        </div>
    );
}

export default MeetingSummary;
